import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import Confetti from 'react-confetti';
import { ThemeProvider, createTheme } from '@mui/material/styles';
import { Box, Typography, Button, Container } from '@mui/material';
import { useSpring, animated } from 'react-spring';

const theme = createTheme({
  palette: {
    primary: {
      main: '#ff69b4', // Pink
    },
    secondary: {
      main: '#9c27b0', // Purple
    },
  },
  typography: {
    fontFamily: "'Roboto', 'Helvetica', 'Arial', sans-serif",
  },
});

function App() {
  const [showConfetti, setShowConfetti] = useState(true);
  const [windowDimensions, setWindowDimensions] = useState({
    width: window.innerWidth,
    height: window.innerHeight,
  });

  useEffect(() => {
    const handleResize = () => {
      setWindowDimensions({
        width: window.innerWidth,
        height: window.innerHeight,
      });
    };
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  const fadeIn = useSpring({
    from: { opacity: 0, transform: 'translateY(50px)' },
    to: { opacity: 1, transform: 'translateY(0)' },
    delay: 500,
  });

  const heartbeat = useSpring({
    from: { transform: 'scale(1)' },
    to: async (next) => {
      while (true) {
        await next({ transform: 'scale(1.1)' });
        await next({ transform: 'scale(1)' });
      }
    },
    config: { duration: 1000 },
  });

  return (
    <ThemeProvider theme={theme}>
      {showConfetti && (
        <Confetti
          width={windowDimensions.width}
          height={windowDimensions.height}
          recycle={false}
          numberOfPieces={200}
          onConfettiComplete={() => setShowConfetti(false)}
        />
      )}
      <Container
        sx={{
          minHeight: '100vh',
          display: 'flex',
          flexDirection: 'column',
          justifyContent: 'center',
          alignItems: 'center',
          background: 'linear-gradient(135deg, #ff69b4 0%, #9c27b0 100%)',
          color: 'white',
          textAlign: 'center',
          padding: 4,
        }}
      >
        <animated.div style={fadeIn}>
          <motion.div
            initial={{ scale: 0 }}
            animate={{ scale: 1 }}
            transition={{ duration: 0.5 }}
          >
            <Typography variant="h2" component="h1" gutterBottom>
              üéâ Happy Birthday! üéâ
            </Typography>
          </motion.div>

          <animated.div style={heartbeat}>
            <Typography variant="h3" component="h2" gutterBottom>
              ‚ù§Ô∏è
            </Typography>
          </animated.div>

          <Box sx={{ my: 4 }}>
            <Typography variant="h4" gutterBottom>
              To my amazing girlfriend
            </Typography>
            <Typography variant="h5" sx={{ mb: 4 }}>
              Thank you for being the most wonderful person in my life.
              Every moment with you is a gift, and I'm so grateful to celebrate another year with you.
            </Typography>
          </Box>

          <motion.div
            whileHover={{ scale: 1.1 }}
            whileTap={{ scale: 0.9 }}
          >
            <Button
              variant="contained"
              color="secondary"
              size="large"
              onClick={() => setShowConfetti(true)}
              sx={{ 
                fontSize: '1.2rem',
                padding: '12px 24px',
                borderRadius: '25px',
                boxShadow: '0 4px 6px rgba(0,0,0,0.1)',
              }}
            >
              Celebrate Again! üéä
            </Button>
          </motion.div>
        </animated.div>
      </Container>
    </ThemeProvider>
  );
}

export default App;
