import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import Confetti from 'react-confetti';
import { ThemeProvider, createTheme } from '@mui/material/styles';
import { Box, Typography, Button, Container, IconButton, Grid } from '@mui/material';
import { useSpring, animated } from 'react-spring';
import { Swiper, SwiperSlide } from 'swiper/react';
import { Navigation, Pagination, EffectCoverflow } from 'swiper/modules';
import { Parallax } from 'react-parallax';
import { useInView } from 'react-intersection-observer';
import Particles from "react-particles";
import { loadFull } from "tsparticles";
import FavoriteIcon from '@mui/icons-material/Favorite';
import PhotoCameraIcon from '@mui/icons-material/PhotoCamera';
import MusicNoteIcon from '@mui/icons-material/MusicNote';

// Import Swiper styles
import 'swiper/css';
import 'swiper/css/navigation';
import 'swiper/css/pagination';
import 'swiper/css/effect-coverflow';

const theme = createTheme({
  palette: {
    primary: {
      main: '#ff69b4',
    },
    secondary: {
      main: '#9c27b0',
    },
  },
  typography: {
    fontFamily: "'Roboto', 'Helvetica', 'Arial', sans-serif",
  },
});

// Sample memories data - replace with your actual memories
const memories = [
  {
    image: "https://source.unsplash.com/random/800x600?couple",
    title: "Our First Date",
    description: "Remember that magical evening..."
  },
  {
    image: "https://source.unsplash.com/random/800x600?love",
    title: "Beach Vacation",
    description: "Those perfect sunset walks..."
  },
  {
    image: "https://source.unsplash.com/random/800x600?romantic",
    title: "Movie Night",
    description: "Cozy times together..."
  },
  // Add more memories here
];

function Section({ children, delay = 0 }) {
  const [ref, inView] = useInView({
    threshold: 0.2,
    triggerOnce: true,
  });

  return (
    <motion.div
      ref={ref}
      initial={{ opacity: 0, y: 50 }}
      animate={inView ? { opacity: 1, y: 0 } : { opacity: 0, y: 50 }}
      transition={{ duration: 0.8, delay }}
    >
      {children}
    </motion.div>
  );
}

function App() {
  const [showConfetti, setShowConfetti] = useState(true);
  const [timeLeft, setTimeLeft] = useState({
    days: 0,
    hours: 0,
    minutes: 0,
    seconds: 0
  });
  const [isTimeUp, setIsTimeUp] = useState(false);
  const [bypassKey, setBypassKey] = useState({
    ctrl: false,
    alt: false,
    b: false,
  });
  const [windowDimensions, setWindowDimensions] = useState({
    width: window.innerWidth,
    height: window.innerHeight,
  });

  const particlesInit = async (engine) => {
    await loadFull(engine);
  };

  const particlesConfig = {
    particles: {
      number: {
        value: 50,
        density: {
          enable: true,
          value_area: 800
        }
      },
      color: {
        value: "#ffffff"
      },
      shape: {
        type: "heart"
      },
      opacity: {
        value: 0.5,
        random: true
      },
      size: {
        value: 5,
        random: true
      },
      move: {
        enable: true,
        speed: 2,
        direction: "none",
        random: true,
        straight: false,
        out_mode: "out",
        bounce: false
      }
    }
  };

  // Bypass key handler
  useEffect(() => {
    const handleKeyDown = (e) => {
      if (e.key === 'Control') {
        setBypassKey(prev => ({ ...prev, ctrl: true }));
      } else if (e.key === 'Alt') {
        setBypassKey(prev => ({ ...prev, alt: true }));
      } else if (e.key.toLowerCase() === 'b') {
        setBypassKey(prev => ({ ...prev, b: true }));
      }
    };

    const handleKeyUp = (e) => {
      if (e.key === 'Control') {
        setBypassKey(prev => ({ ...prev, ctrl: false }));
      } else if (e.key === 'Alt') {
        setBypassKey(prev => ({ ...prev, alt: false }));
      } else if (e.key.toLowerCase() === 'b') {
        setBypassKey(prev => ({ ...prev, b: false }));
      }
    };

    window.addEventListener('keydown', handleKeyDown);
    window.addEventListener('keyup', handleKeyUp);

    return () => {
      window.removeEventListener('keydown', handleKeyDown);
      window.removeEventListener('keyup', handleKeyUp);
    };
  }, []);

  // Check for bypass combination
  useEffect(() => {
    if (bypassKey.ctrl && bypassKey.alt && bypassKey.b) {
      setIsTimeUp(true);
    }
  }, [bypassKey]);

  useEffect(() => {
    const targetDate = new Date('2025-05-07T00:00:00');

    const calculateTimeLeft = () => {
      const now = new Date();
      const difference = targetDate - now;

      if (difference <= 0) {
        setIsTimeUp(true);
        return;
      }

      const days = Math.floor(difference / (1000 * 60 * 60 * 24));
      const hours = Math.floor((difference % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60));
      const minutes = Math.floor((difference % (1000 * 60 * 60)) / (1000 * 60));
      const seconds = Math.floor((difference % (1000 * 60)) / 1000);

      setTimeLeft({ days, hours, minutes, seconds });
    };

    calculateTimeLeft();
    const timer = setInterval(calculateTimeLeft, 1000);

    return () => clearInterval(timer);
  }, []);

  useEffect(() => {
    const handleResize = () => {
      setWindowDimensions({
        width: window.innerWidth,
        height: window.innerHeight,
      });
    };
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  const fadeIn = useSpring({
    from: { opacity: 0, transform: 'translateY(50px)' },
    to: { opacity: 1, transform: 'translateY(0)' },
    delay: 500,
  });

  const heartbeat = useSpring({
    from: { transform: 'scale(1)' },
    to: async (next) => {
      while (true) {
        await next({ transform: 'scale(1.1)' });
        await next({ transform: 'scale(1)' });
      }
    },
    config: { duration: 1000 },
  });

  if (!isTimeUp) {
    return (
      <ThemeProvider theme={theme}>
        <Container
          sx={{
            minHeight: '100vh',
            display: 'flex',
            flexDirection: 'column',
            justifyContent: 'center',
            alignItems: 'center',
            background: 'linear-gradient(135deg, #ff69b4 0%, #9c27b0 100%)',
            color: 'white',
            textAlign: 'center',
            padding: 4,
            position: 'relative',
            overflow: 'hidden',
          }}
        >
          <Particles
            init={particlesInit}
            options={particlesConfig}
            style={{
              position: 'absolute',
              top: 0,
              left: 0,
              width: '100%',
              height: '100%',
              zIndex: 0,
            }}
          />
          <Box sx={{ position: 'relative', zIndex: 1 }}>
            <Typography variant="h3" gutterBottom>
              Coming Soon...
            </Typography>
            <Box
              sx={{
                display: 'flex',
                gap: 4,
                flexWrap: 'wrap',
                justifyContent: 'center',
                my: 4,
              }}
            >
              <Box sx={{ textAlign: 'center', minWidth: '120px' }}>
                <Typography variant="h2">{timeLeft.days}</Typography>
                <Typography variant="h6">Days</Typography>
              </Box>
              <Box sx={{ textAlign: 'center', minWidth: '120px' }}>
                <Typography variant="h2">{timeLeft.hours}</Typography>
                <Typography variant="h6">Hours</Typography>
              </Box>
              <Box sx={{ textAlign: 'center', minWidth: '120px' }}>
                <Typography variant="h2">{timeLeft.minutes}</Typography>
                <Typography variant="h6">Minutes</Typography>
              </Box>
              <Box sx={{ textAlign: 'center', minWidth: '120px' }}>
                <Typography variant="h2">{timeLeft.seconds}</Typography>
                <Typography variant="h6">Seconds</Typography>
              </Box>
            </Box>
            <Typography variant="h5">
              Until the Special Day! 🎉
            </Typography>
          </Box>
        </Container>
      </ThemeProvider>
    );
  }

  return (
    <ThemeProvider theme={theme}>
      <AnimatePresence>
        {showConfetti && (
          <Confetti
            width={windowDimensions.width}
            height={windowDimensions.height}
            recycle={false}
            numberOfPieces={200}
            onConfettiComplete={() => setShowConfetti(false)}
          />
        )}
        
        {/* Hero Section */}
        <Parallax
          blur={0}
          bgImage="https://source.unsplash.com/random/1920x1080?romantic"
          strength={200}
          style={{ height: '100vh' }}
        >
          <Container
            sx={{
              height: '100vh',
              display: 'flex',
              flexDirection: 'column',
              justifyContent: 'center',
              alignItems: 'center',
              color: 'white',
              textAlign: 'center',
              padding: 4,
              background: 'rgba(0, 0, 0, 0.4)',
            }}
          >
            <animated.div style={fadeIn}>
              <motion.div
                initial={{ scale: 0 }}
                animate={{ scale: 1 }}
                transition={{ duration: 0.5 }}
              >
                <Typography variant="h1" component="h1" gutterBottom>
                  🎉 Happy Birthday! 🎉
                </Typography>
              </motion.div>

              <animated.div style={heartbeat}>
                <Typography variant="h2" component="h2" gutterBottom>
                  ❤️
                </Typography>
              </animated.div>
            </animated.div>
          </Container>
        </Parallax>

        {/* Message Section */}
        <Section>
          <Container sx={{ py: 8 }}>
            <Typography variant="h3" gutterBottom align="center">
              To My Amazing Girlfriend
            </Typography>
            <Typography variant="h5" align="center" paragraph>
              Every moment with you is a gift, and I'm so grateful to celebrate another year with you.
              Your smile brightens my darkest days, and your love makes every moment special.
            </Typography>
          </Container>
        </Section>

        {/* Photo Gallery */}
        <Section delay={0.2}>
          <Box sx={{ bgcolor: 'grey.100', py: 8 }}>
            <Container>
              <Typography variant="h3" gutterBottom align="center">
                Our Memories Together
              </Typography>
              <Swiper
                effect={'coverflow'}
                grabCursor={true}
                centeredSlides={true}
                slidesPerView={'auto'}
                coverflowEffect={{
                  rotate: 50,
                  stretch: 0,
                  depth: 100,
                  modifier: 1,
                  slideShadows: true,
                }}
                pagination={true}
                navigation={true}
                modules={[EffectCoverflow, Pagination, Navigation]}
                className="mySwiper"
                style={{ padding: '50px 0' }}
              >
                {memories.map((memory, index) => (
                  <SwiperSlide key={index} style={{ width: '300px', height: '400px' }}>
                    <Box
                      sx={{
                        position: 'relative',
                        height: '100%',
                        borderRadius: '15px',
                        overflow: 'hidden',
                      }}
                    >
                      <img
                        src={memory.image}
                        alt={memory.title}
                        style={{
                          width: '100%',
                          height: '100%',
                          objectFit: 'cover',
                        }}
                      />
                      <Box
                        sx={{
                          position: 'absolute',
                          bottom: 0,
                          left: 0,
                          right: 0,
                          background: 'rgba(0,0,0,0.7)',
                          color: 'white',
                          padding: 2,
                        }}
                      >
                        <Typography variant="h6">{memory.title}</Typography>
                        <Typography variant="body2">{memory.description}</Typography>
                      </Box>
                    </Box>
                  </SwiperSlide>
                ))}
              </Swiper>
            </Container>
          </Box>
        </Section>

        {/* Features Section */}
        <Section delay={0.4}>
          <Container sx={{ py: 8 }}>
            <Grid container spacing={4} justifyContent="center">
              <Grid item xs={12} md={4}>
                <Box sx={{ textAlign: 'center', p: 3 }}>
                  <IconButton
                    sx={{
                      bgcolor: 'primary.main',
                      color: 'white',
                      '&:hover': { bgcolor: 'primary.dark' },
                      mb: 2,
                    }}
                  >
                    <FavoriteIcon fontSize="large" />
                  </IconButton>
                  <Typography variant="h5" gutterBottom>
                    Endless Love
                  </Typography>
                  <Typography>
                    Every day my love for you grows stronger
                  </Typography>
                </Box>
              </Grid>
              <Grid item xs={12} md={4}>
                <Box sx={{ textAlign: 'center', p: 3 }}>
                  <IconButton
                    sx={{
                      bgcolor: 'secondary.main',
                      color: 'white',
                      '&:hover': { bgcolor: 'secondary.dark' },
                      mb: 2,
                    }}
                  >
                    <PhotoCameraIcon fontSize="large" />
                  </IconButton>
                  <Typography variant="h5" gutterBottom>
                    Beautiful Memories
                  </Typography>
                  <Typography>
                    Capturing our special moments together
                  </Typography>
                </Box>
              </Grid>
              <Grid item xs={12} md={4}>
                <Box sx={{ textAlign: 'center', p: 3 }}>
                  <IconButton
                    sx={{
                      bgcolor: 'primary.main',
                      color: 'white',
                      '&:hover': { bgcolor: 'primary.dark' },
                      mb: 2,
                    }}
                  >
                    <MusicNoteIcon fontSize="large" />
                  </IconButton>
                  <Typography variant="h5" gutterBottom>
                    Sweet Melodies
                  </Typography>
                  <Typography>
                    The soundtrack of our love story
                  </Typography>
                </Box>
              </Grid>
            </Grid>
          </Container>
        </Section>

        {/* Celebration Button */}
        <Section delay={0.6}>
          <Container sx={{ py: 8, textAlign: 'center' }}>
            <motion.div
              whileHover={{ scale: 1.1 }}
              whileTap={{ scale: 0.9 }}
            >
              <Button
                variant="contained"
                color="secondary"
                size="large"
                onClick={() => setShowConfetti(true)}
                sx={{ 
                  fontSize: '1.2rem',
                  padding: '12px 24px',
                  borderRadius: '25px',
                  boxShadow: '0 4px 6px rgba(0,0,0,0.1)',
                }}
              >
                Celebrate Again! 🎊
              </Button>
            </motion.div>
          </Container>
        </Section>
      </AnimatePresence>
    </ThemeProvider>
  );
}

export default App;
